import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';

export function Weather({}) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`/v1/places`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`This is an error: The status is ${response.status}`);
        }
        return response.json();
      })
      .then((actualData) => {
        setData(actualData);
        setError(null);
      })
      .catch((err) => {
        setError(err.message);
        setData(null);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  //   airTemperature, forecastTimeUtc
  const [weather, setWeather] = useState(null);

  async function getLocationWeather(e) {
    const city = e.target.value;
    const currentWeatherFetch = await fetch(`v1/places/${city}/forecasts/long-term`);
    console.log(city)

    Promise.all([currentWeatherFetch])
    .then(async (response) => {
        const weatherResponse = await response[0].json();
        setWeather({weatherResponse})
    })
    .catch((err) => console.log(err))

    const currentWeather = weatherResponse.forecastTimestamps.filter((timeUtc) => {
        console.log(timeUtc.forecastTimeUtc);
        return timeUtc.forecastTimeUtc;
    })

  }

  console.log(weather)
 
//   console.log(getLocationWeather('vilnius'));

  return (
    <div className=" text-center p-2 m-auto">
      <h3 className="p-3">Select city to check weather</h3>
      {loading && <div>A moment please...</div>}
      {error && <div>{`There is a problem fetching the post data - ${error}`}</div>}

      <select name="city" onChange={getLocationWeather} className="form-control text-center">
        <option value="">Select City</option>
        {data && data.map(({ code, name }) => <option key={code}>{name}</option>)}
      </select>
     
      <h4 className="pt-4">Temperature: {JSON.stringify(weather)}  &deg;C</h4>
    </div>
  );
}


