import './App.css';
import React from 'react';
import { Hero } from './Hero.jsx';
import { AppList } from './AppList';
import { useState } from 'react';
import { SelectedApp } from './SelectedApp';
import { Counter } from './Counter';
import { Weather } from './Weather';
import { ThemeProvider } from './ThemeContext'
import { useTheme, useThemeUpdate } from './ThemeContext';

import 'bootstrap/dist/css/bootstrap.css';

function App() {
  const [selectedApp, setApp] = useState(null);
  const darkTheme = useTheme();

  const themeStyles = {
    backgroundColor: darkTheme ? '#333' : '#ccc',
    color: darkTheme ? '#ccc' : '#333',
 }

  return (
    <div>
      <ThemeProvider>
        <Hero></Hero>
        {selectedApp ? (
          <SelectedApp appName={selectedApp} onBackClick={() => setApp(null)} style={themeStyles}>
            <b>{selectedApp === 'Counter' ? <Counter /> : 'I am a text child node for children prop'}</b>
            <p>{selectedApp === 'Weather' ? <Weather/> : '' }</p>
          </SelectedApp>
        ) : (
          <AppList setApplication={setApp} />
        )}
        </ThemeProvider>
    </div>
  );
}

export default App;
