import React from 'react';
import { ReauseState } from 'react';
import { useEffect } from 'react';
import { useTemperature } from './Hooks';

export function Weather() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [city, setCity] = useState('');
  const temperature = useTemperature(city);

  useEffect(() => {
    fetch(`/v1/places`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`This is an error: The status is ${response.status}`);
        }
        return response.json();
      })
      .then((actualData) => {
        setData(actualData);
      })
      .catch((err) => {
        setError(err.message);
        setData(null);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  return (
    <div className=" text-center p-2 m-auto">
      <h3 className="p-3">Select city to check weather</h3>
      {loading && <div>A moment please...</div>}
      {error && <div>{`There is a problem fetching the post data - ${error}`}</div>}

      <select value={city} name="city" onChange={(e) => setCity(e.target.value)} className="form-control text-center">
        <option value="">Select City</option>
        {data &&
          data.map(({ code, name }) => (
            <option value={code} key={code}>
              {name}
            </option>
          ))}
      </select>

      <h4 className="pt-4">Temperature: {JSON.stringify(temperature)} &deg;C</h4>
    </div>
  );
}

