import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';

export function Weather(props) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`/v1/places`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`This is an error: The status is ${response.status}`);
        }
        return response.json();
      })
      .then((actualData) => {
        setData(actualData);
        setError(null);
      })
      .catch((err) => {
        setError(err.message);
        setData(null);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  //   const [serverUrl, setServerUrl] = useState('https://api.meteo.lt');
//   airTemperature, forecastTimeUtc
  async function getLocationWeather(location) {
    const result = await fetch(`v1/places/${location}/forecasts/long-term`);
    return result.json();
  }
  console.log(getLocationWeather('vilnius'))
    const [weather, setWeather] = useState('');


    React.useEffect(() => {
        const getWeather = async () => {
          const result = await getLocationWeather(location);
          setWeatherData(result.success ? result.data : {});

        };

        getWeather();
      }, [location]);

      const { temp, description, icon, windTransform, windSpeed } = React.useMemo(() => {
        const [weather] = weatherData.weather || [];
        return {
          temp: weatherData.main && weatherData.main.temp ? Math.round(weatherData.main.temp).toString() : "",
          description: weather ? weather.description : "",
          icon: weather ? `http://openweathermap.org/img/wn/${weather.icon}@2x.png` : "",
          windTransform: weatherData.wind ? weatherData.wind.deg - 90 : null,
          windSpeed: weatherData.wind ? Math.round(weatherData.wind.speed) : 0,
        };
      }, [weatherData]);



  return (
    <div className=" text-center p-2 m-auto">
      {/* <h3>Weather:</h3>
      <p>{weather}</p>
      <button className="btn btn-dark" onClick={() => setWeather()}>
        Show current weather
      </button> */}
      <h3 className='p-3'>Select city to check weather</h3>
      {loading && <div>A moment please...</div>}
      {error && <div>{`There is a problem fetching the post data - ${error}`}</div>}

      <select name='city' onChange={(e) => (e)} className='form-control text-center'>
        <option value=''>Select City</option>
        {data &&
          data.map(({ code, name }) => (
            <option key={code}>
              {name}
            </option>
          ))}
      </select>

      <h4 className='pt-4'>Temperature is { weather } C
      </h4>
    </div>
  );
}
