import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';

export function Weather() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState('');

  useEffect(() => {
    fetch(`/v1/places`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`This is an error: The status is ${response.status}`);
        }
        return response.json();
      })
      .then((actualData) => {
        setData(actualData);
      })
      .catch((err) => {
        setError(err.message);
        setData(null);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    if (!city) {
      return;
    }
    const myAbortController = new AbortController();

    (async () => {
      try {
        const currentWeatherFetch = await fetch(`v1/places/${city}/forecasts/long-term`, );
        const json = await currentWeatherFetch.json();
        const currentWeather = json.forecastTimestamps.filter((timeUtc) => {
          return timeUtc.forecastTimeUtc;
        });
        setWeather(currentWeather[0].airTemperature);
      } catch (e) {
        console.log(e);
      }
    })();
  }, [city]);

  return (
    <div className=" text-center p-2 m-auto">
      <h3 className="p-3">Select city to check weather</h3>
      {loading && <div>A moment please...</div>}
      {error && <div>{`There is a problem fetching the post data - ${error}`}</div>}

      <select value={city} name="city" onChange={(e) => setCity(e.target.value)} className="form-control text-center">
        <option value="">Select City</option>
        {data &&
          data.map(({ code, name }) => (
            <option value={code} key={code}>
              {name}
            </option>
          ))}
      </select>

      <h4 className="pt-4">Temperature: {JSON.stringify(weather)} &deg;C</h4>
    </div>
  );
}

