import React from 'react';
import { useState } from 'react';

export function Counter(props) {
    const [counter, setCount] = useState(0)
  return (
    <div>
        <h3>Times clicked:</h3>
        <p>{counter}</p>
        <button onClick={() => setCount(prevState=> prevState +1)}>Click me</button>
    </div>
  )
}
