    fetch(`/v1/places`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`This is an error: The status is ${response.status}`);
        }
        return response.json();
      })
      .then((actualData) => {
        setData(actualData);
      })
      .catch((err) => {
        setError(err.message);
