import './App.css';
import React from 'react';
import { Hero } from './Hero.jsx';
import { AppList } from './AppList';
import { useState } from 'react';
import { SelectedApp } from './SelectedApp';
import { Counter } from './Counter';
import { Weather } from './Weather';
impor
import 'bootstrap/dist/css/bootstrap.css';

function App() {
  const [selectedApp, setApp] = useState(null);
  const [darkTheme, setDarkTheme] = useState(true)

  function toggleTheme() {
    setDarkTheme(prevDarkTheme => !prevDarkTheme)
  }

  return (
    <div>
      <ThemeProvider>
          <Hero>
            <button onClick={toggleTheme}>Toggle</button>
          </Hero>
          {selectedApp ? (
            <SelectedApp appName={selectedApp} onBackClick={() => setApp(null)}>
              <b>{selectedApp === 'Counter' ? <Counter /> : 'I am a text child node for children prop'}</b>
              <p>{selectedApp === 'Weather' ? <Weather/> : '' }</p>
            </SelectedApp>
          ) : (
            <AppList setApplication={setApp} />
          )}
        </ThemeProvider>
    </div>
  );
}

export default App;
