import './App.css';
import { AppCard } from './AppCard';
import pic1 from './img/pic1.jpg';
import pic2 from './img/pic2.jpg';
import pic3 from './img/pic3.jpg';
import pic4 from './img/pic4.jpg';
import pic5 from './img/pic5.jpg';
import pic6 from './img/pic6.jpg';
import { useTheme, useThemeUpdate } from './ThemeContext';


export function AppList(props) {
  const darkTheme = useTheme();

  const themeStyles = {
    backgroundColor: darkTheme ? '#333' : '#ccc',
    color: darkTheme ? '#ccc' : '#333',
 }
  return (
    <div className="grid b" style={themeStyles}>
      <AppCard onClick={() => props.setApplication('Counter')} img={pic1} text={'Counter'} />
      <AppCard onClick={() => props.setApplication('Weather')} img={pic2} text={'Weather'} />
      <AppCard onClick={() => props.setApplication('Click app-3')} img={pic3} text={'App-3'} />
      <AppCard onClick={() => props.setApplication('Click app-4')} img={pic4} text={'App-4'} />
      <AppCard onClick={() => props.setApplication('Click app-5')} img={pic5} text={'App-5'} />
      <AppCard onClick={() => props.setApplication('Click app-6')} img={pic6} text={'App-6'} />
    </div>
  );
}
