import React from 'react';
import { useState } from 'react';
import { useTheme } from './ThemeContext';


export function Counter(props) {

  const [counter, setCount] = useState(0);

  const [selectedApp, setApp] = useState(null);
  const darkTheme = useTheme();

  const themeStyles = {
    backgroundColor: darkTheme ? 'rgb(211 255 214 / 67%)' : 'rgb(44 58 45 / 86%)',
    color: darkTheme ? 'rgb(78 74 74)' : 'rgb(226 225 225)',
 }

  return (
      <div className="counter text-center p-2 m-auto" style={themeStyles}>
        <h3>Times clicked:</h3>
        <p>{counter}</p>
        <button className="btn btn-dark" onClick={() => setCount((prevState) => prevState + 1)}>
          Click me
        </button>
      </div>
  );
}
