import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';

export function Weather(props) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`/v1/places`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`This is an error: The status is ${response.status}`);
        }
        return response.json();
      })
      .then((actualData) => {
        setData(actualData);
        setError(null);
      })
      .catch((err) => {
        setError(err.message);
        setData(null);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  //   const [serverUrl, setServerUrl] = useState('https://api.meteo.lt');

    const [weather, setWeather] = useState({temp: ''});
    const [temperature, setTemperature] = useState([]);

    const cityTemp = async (e) => {
        const selectedCity = e.target.value;
        const temp = await fetch(`/v1/places/${selectedCity}/forecasts/long-term`);
        const placeTemp = await temp.json();
        //get current date


        const filterArr = placeTemp.forecastTimestamps.filter((place) => place.forecastTimeUtc === `${forcastDate}`);
        setTemperature(filterArr[0].airTemperature);
      };
  return (
    <div className=" text-center p-2 m-auto">
      {/* <h3>Weather:</h3>
      <p>{weather}</p>
      <button className="btn btn-dark" onClick={() => setWeather()}>
        Show current weather
      </button> */}
      <h3 className='p-3'>Select city to check weather</h3>
      {loading && <div>A moment please...</div>}
      {error && <div>{`There is a problem fetching the post data - ${error}`}</div>}

      <select name='city' onChange={(e) => cityTemp(e)} className='form-control text-center'>
        <option value=''>Select City</option>
        {data &&
          data.map(({ code, name }) => (
            <option key={code}>
              {name}
            </option>
          ))}
      </select>

      <h4 className='pt-4'>Temperature is { temperature } C</h4>
    </div>
  );
}
