import { useEffect } from 'react';
import { React, useState, useRef } from 'react';

export function Inputs() {
  const inputRef = useRef(null);
  const inputRef2 = useRef(null);
  const [value, setValue] = useState('')
  const [value2, setValue2] = useState('')
  const [add, setAdd] = useState('');
  const [deduct, setDeduct] = useState('');
  const [multiply, setMultiply] = useState('');
  const [divide, setDivide] = useState('');

  // function handleValueCjange(e) {

  //   if (value === add) {
  //   add = {Number(value) + Number(value2)}
  //   }
  // }
  function handleSubmit(e) {
    e.preventDefault();
    console.log('You clicked submit.');
    console.log(inputRef.current.value);
    console.log(inputRef2.current.value)
  }

  return (
    <div className="row">
      <div className="col">
        <div className="fst-italic my-2">Uncontrolled math</div>
        <div className="calculations align-items-center justify-content-center">
          <div className="py-2 row d-flex m-2">
            <input ref={inputRef} type={'number'} className="col "/>
            <select className="col ">
              <option value="+">+</option>
              <option value="-">-</option>
              <option value="*">*</option>
              <option value="/">/</option>
            </select>
            <input ref={inputRef2} type={'number'} className="col "/>
          </div>

          <button onClick={handleSubmit} className="btn btn-secondary w-50" type='submit'>Submit</button>
        </div>
        <div className="result my-2">Result: </div>
      </div>
      <div className="col">
        <div className="fst-italic my-2">Controlled math</div>
        <div className="calculations my-3">
          <div className="">
            <div className="row m-2">
            <input type={'number'} value={value} onChange={(e) => setValue(e.target.value)} className="col"/>
              <select className="col">
                <option value={Number(value) + Number(value2)}>+</option>
                <option value={Number(value) - Number(value2)}>-</option>
                <option value={multiply}>*</option>
                <option value={divide}>/</option>
              </select>
              <input type={'number'} value={value2} onChange={(e) => setValue2(e.target.value) }className="col"/>
            </div>
            <button onClick={() => {setValue('')&& setValue2('')}} className="btn btn-secondary w-50" >Reset</button>
            {/* add: {Number(value) + Number(value2)} */}

          </div>
        </div>
        <div className="result my-2">Result: </div>
      </div>
    </div>
  );
}
